mapper.select:
select oi.id, oi.code, oi.user_id, oi.merchant_id, oi.order_price, oi.after_order_price, oi.discount, oi.integral, oi.status, oi.`type`, oi.kilometre, oi.distribution_price, oi.create_date, oi.pay_date, oi.service_date, oi.address_id, oi.staff_id from order_info oi where 1 = 1
AND oi.merchant_id = #{merchantId}
and oi.status != '0' and DATE_FORMAT( oi.create_date, '%Y%m' ) = DATE_FORMAT( CURDATE(), '%Y%m')

select oi.id, oi.code, oi.user_id, oi.merchant_id, oi.order_price, oi.after_order_price, oi.discount, oi.integral, oi.status, oi.`type`, oi.kilometre, oi.distribution_price, oi.create_date, oi.pay_date, oi.service_date, oi.address_id, oi.staff_id from order_info oi where 1 = 1
AND oi.merchant_id = #{merchantId}
and oi.status != '0' and DATE_FORMAT( oi.create_date, '%Y' ) = DATE_FORMAT( CURDATE(), '%Y')

SELECT DATE_FORMAT( spo.days, '%m-%d' ) AS days, IFNULL(COUNT(oi.id), 0) AS count FROM ( SELECT DATE_SUB( curdate(), INTERVAL + 0 DAY ) days UNION SELECT DATE_SUB( curdate(), INTERVAL + 1 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 2 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 3 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 4 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 5 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 6 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 7 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 8 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 9 DAY )) spo LEFT JOIN order_info oi ON ( DATE_FORMAT( oi.create_date, '%Y-%m-%d' ) = DATE_FORMAT( spo.days, '%Y-%m-%d' )
AND oi.merchant_id = #{merchantId}
AND oi.status != '0' ) GROUP BY days ORDER BY days ASC

SELECT DATE_FORMAT( spo.days, '%m-%d' ) AS days, IFNULL(SUM(oi.after_order_price), 0) AS price FROM ( SELECT DATE_SUB( curdate(), INTERVAL + 0 DAY ) days UNION SELECT DATE_SUB( curdate(), INTERVAL + 1 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 2 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 3 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 4 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 5 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 6 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 7 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 8 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 9 DAY )) spo LEFT JOIN order_info oi ON ( DATE_FORMAT( oi.create_date, '%Y-%m-%d' ) = DATE_FORMAT( spo.days, '%Y-%m-%d' )
AND oi.merchant_id = #{merchantId}
AND oi.status != '0' ) GROUP BY days ORDER BY days ASC
